import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUpload, faEdit, faDownload, faTrash } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [datlist, setDataList] = useState([]);
  const fileInputRef = React.createRef();
  const apiUrl = "http://localhost:3001";
  const Navigate = useNavigate();

  const fetchData = async () => {
    const userId = localStorage.getItem('userlogin');
    try {
      const responseFiles = await axios.get(`${apiUrl}/files/${userId}`);
      setDataList(responseFiles.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleFileChange = async (e) => {
    const userId = localStorage.getItem('userlogin');
    const selectedFile = e.target.files;
    const formData = new FormData()
    formData.append('file', selectedFile[0]);
    formData.append('user', userId)
    try {
      await axios.post(`${apiUrl}/upload`, formData);
      fetchData();
    } catch (error) {
      console.log('Error uploading file', error);
    }
  };

  const editFile = () => {
    Navigate('/Edit');
  };

  const deleteFile = async (id) => {
    try {
      await axios.delete(`${apiUrl}/deleteUser/${id}`);
      fetchData(); // Refresh the data after deletion
    } catch (error) {
      console.log('Error deleting file', error);
    }
  };

  const handleRemoveAndReupload = async (id) => {
    await deleteFile(id); // Remove the existing file
    fileInputRef.current.click(); // Trigger file input to re-upload
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <input type="file" ref={fileInputRef} style={{ display: 'none' }} accept=".doc, .docx, .pdf, .xlsx, .jpg, .jpeg, .png" onChange={handleFileChange} />
      <button type="button" onClick={() => handleRemoveAndReupload(datlist[0]._id)} className="btn btn-primary mx-5 mt-1">
        <FontAwesomeIcon icon={faUpload} /> Upload
      </button>

      <table className='direct'>
        <thead>
          <tr>
            <th className='center'>S.No</th>
            <th className='center'>List</th>
            <th colSpan={3} className='center'>Action</th>
          </tr>
        </thead>
        <tbody>
          {datlist.map((val, index) => (
            <tr key={val._id}>
              <td>{index + 1}</td>
              <td>{val.filename}</td>
              <td> <button className="btn btn-outline-success" onClick={() => editFile()}><FontAwesomeIcon icon={faEdit} /> Edit</button></td>
              <td><button className="btn btn-outline-success mx-3"><FontAwesomeIcon icon={faDownload} /> Download</button></td>
              <td>
                <button className="btn btn-outline-danger" onClick={() => handleRemoveAndReupload(val._id)}>
                  <FontAwesomeIcon icon={faTrash} /> Delete & Reupload
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
