// EditFile.js
import React, { useState } from 'react';
import axios from 'axios';

const EditFile = ({ fileId, fetchData, closeModal }) => {
  const [newFileName, setNewFileName] = useState('');

  const handleEdit = async () => {
    try {
      await axios.put(`http://localhost:3001/editFile/${fileId}`, { filename: newFileName });
      fetchData(); // Refresh the data after editing
      closeModal(); // Close the edit modal or navigate back
    } catch (error) {
      console.error('Error editing file', error);
    }
  };

  return (
    <div>
      <label>Edit File Name:</label>
      <input type="text" value={newFileName} onChange={(e) => setNewFileName(e.target.value)} />
      <button onClick={handleEdit}>Save</button>
    </div>
  );
};

export default EditFile;
